import React, { useState } from "react";
import { Button, Row, Col, Container } from 'react-bootstrap';
import Input from '../../InputBase';
import DropdownBase from "../../DropdownBase";

// Whenever we try to render an array containing JSX, React knows to render each JSX element separately

const quantityOptions = ['lbs.', 'oz.', 'dz.', ];
const catagoryOptions = ['Produce', 'Meat', 'Dairy','Non-Parishable', 'Spices', 'One-Off', 'Seasonal'];


function ExpenseInsert(props) {

  const [text, setText] = useState({
    item_name: '',
    quantity: '',
    cost: ''

  });

  const [catagory, setCatagory] = useState(catagoryOptions[0]);
  const [quantityType, setQuantityType] = useState(quantityOptions[0]);


  const handleInputChange = (e) => {
    e.preventDefault();
    const { name, value } = e.target;

    setText((prevState) => ({
      ...prevState, [name]: value
     })
    )
  };

  const invObj = {
    ...text,
    catagory,
    quantityType
  }


  const style = {
    container: {
      justifyContent: 'center'
    },
    itemLabel: {
        
      textAlign: 'right',
      alignSelf: 'stretch',
      width:'90px',
      margin: '5px'
    },
    inputStyle: {
      marginTop: '2px',
      padding: '0',
      width: '125px'
    },
    inputCostStyle: {
      width: '60px',
      margin: '0',
      padding: '0'
    },
    dropdownStyle: {
      marginLeft: '4px',
      height: '15px'
    },
    button: {
      marginLeft: '4px',
      }
    
  };
  

  return (
    <Container style={style.container}>
        <Row>
          <p style={style.itemLabel}>Expense Name:</p>
          <Input style={style.inputStyle} placeholder='enter item name' inputLabel='Item Name' handleInputChange={handleInputChange} name='item_name' value={ text.itemName }/>
          <DropdownBase buttonLabel={'select expense catagory'} style={style.dropdownStyle} handleDropdownChange={setCatagory} name='catagory' value={catagory} options={catagoryOptions} />
        </Row>
        <Row>
          <DropdownBase buttonLabel={'select unit'} style={style.dropdownStyle} handleDropdownChange={setQuantityType} name='unit' value={quantityType} options={quantityOptions} />    
        </Row>
        <Row>
          <p style={style.itemLabel}>Cost: $</p>
          <Input style={style.inputCostStyle} placeholder='0000.00' handleInputChange={handleInputChange} name='cost' ></Input>
          <Button variant='primary' size='sm' style={style.button} onClick={() => props.inventoryAjaxPost(invObj)}>Submit Item to Inventory</Button> 
        </Row> 
    </Container>
  );
};

export default ExpenseInsert;
